// Enhanced job data validation and normalization
const jobs = items.map((item, index) => {
  const jobData = item.json;

  // Handle different API response structures
  let normalizedJob = {};

  // Jooble API structure
  if (jobData.jobs && Array.isArray(jobData.jobs)) {
    return jobData.jobs.map(job => ({
      json: {
        id: job.id || `jooble_${index}_${Date.now()}`,
        title: job.title || 'Untitled Position',
        company: job.company || 'Unknown Company', 
        location: job.location || 'Location not specified',
        description: job.snippet || job.description || 'No description available',
        url: job.link || '#',
        posted_date: job.updated || new Date().toISOString(),
        source: 'Jooble',
        salary: job.salary || 'Not specified',
        employment_type: job.type || 'Full-time',
        validation_score: 100
      }
    }));
  }

  // JobStreet API structure  
  if (jobData.data && jobData.data.jobs) {
    return jobData.data.jobs.map(job => ({
      json: {
        id: job.id || `jobstreet_${index}_${Date.now()}`,
        title: job.jobTitle || job.title || 'Untitled Position',
        company: job.company?.name || job.company || 'Unknown Company',
        location: job.jobLocation?.displayName || job.location || 'Location not specified', 
        description: job.jobSummary || job.description || 'No description available',
        url: job.jobDetailsUrl || job.url || '#',
        posted_date: job.postedAt || new Date().toISOString(),
        source: 'JobStreet',
        salary: job.salary?.displayText || 'Not specified',
        employment_type: job.workType || 'Full-time',
        validation_score: 100
      }
    }));
  }

  // Indeed/WhatJobs structure (HTML or different API)
  normalizedJob = {
    id: jobData.id || `job_${index}_${Date.now()}`,
    title: jobData.title || jobData.jobTitle || jobData.position || 'Untitled Position',
    company: jobData.company?.name || jobData.company || jobData.employer || 'Unknown Company',
    location: jobData.location?.displayName || jobData.location || jobData.city || 'Location not specified',
    description: jobData.description || jobData.summary || jobData.snippet || 'No description available',
    url: jobData.url || jobData.link || jobData.application_url || '#',
    posted_date: jobData.postedAt || jobData.datePosted || new Date().toISOString(),
    source: jobData.source || 'API',
    salary: jobData.salary || jobData.salaryRange || 'Not specified',
    employment_type: jobData.employmentType || jobData.jobType || 'Full-time',
    requirements: jobData.requirements || jobData.skills || [],
    benefits: jobData.benefits || [],
    raw_data: jobData
  };

  // Calculate validation score
  normalizedJob.validation_score = 0;
  if (normalizedJob.title && normalizedJob.title !== 'Untitled Position') normalizedJob.validation_score += 25;
  if (normalizedJob.company && normalizedJob.company !== 'Unknown Company') normalizedJob.validation_score += 25;
  if (normalizedJob.description && normalizedJob.description !== 'No description available') normalizedJob.validation_score += 25;
  if (normalizedJob.url && normalizedJob.url !== '#') normalizedJob.validation_score += 25;

  return { json: normalizedJob };
}).flat();

// Filter valid jobs and ensure we have an array
const validJobs = Array.isArray(jobs) ? jobs.filter(job => 
  job.json && job.json.validation_score >= 50
) : [];

return validJobs.length > 0 ? validJobs : [{ 
  json: { 
    error: 'No valid jobs found', 
    total_processed: items.length,
    message: 'Check API responses and data structure'
  } 
}];

